<?php/****************************** * Author      : Rainulf      * * Date Started: Oct 19, 2010 * * Last Updated: Feb 24, 2011 * ******************************/// REF: http://www.php.net/manual/en/function.set-error-handler.phpfunction customErrorHandler($errno, $errstr, $errfile, $errline) {   $ret = null;   if (!(error_reporting( ) & $errno)) {        $ret = false;   }   else {      echo "<script>alert('";      switch ($errno) {         case E_USER_ERROR:            echo "<b>ERROR #{$errno}:</b> $errstr<br />\n";            echo "  Fatal error on line $errline in file $errfile";            echo ", PHP " . PHP_VERSION . " (" . PHP_OS . ")<br />\n";            echo "Aborting...<br />\n";            exit(1);            break;         case E_USER_WARNING:            echo "WARNING #{$errno}: $errstr at line $errline in file $errfile<br />\n";            break;         case E_USER_NOTICE:            echo "$errstr.";            break;         default:            echo "Something went wrong: $errstr at line $errline in file $errfile<br />\n";            break;      }      echo "'); </script>";      $ret = true;   }    /* Don't execute PHP internal error handler */    return $ret;} ?>